      const userId = req.user.claims.sub;

      // Validate input using Zod schema
      const addressSchema = z.object({
        name: z.string().min(1, "Nome √© obrigat√≥rio"),
        cep: z.string().regex(/^\d{8}$|^\d{5}-?\d{3}$/, "CEP inv√°lido"),
        street: z.string().min(1, "Rua √© obrigat√≥ria"),
        number: z.string().min(1, "N√∫mero √© obrigat√≥rio"),
        complement: z.string().optional(),
        neighborhood: z.string().min(1, "Bairro √© obrigat√≥rio"),
        city: z.string().min(1, "Cidade √© obrigat√≥ria"),
        state: z.string().min(2, "Estado √© obrigat√≥rio").max(2, "Estado deve ter 2 caracteres"),
        isDefault: z.boolean().optional().default(false)
      });

      const validatedData = addressSchema.parse(req.body);
      const addressData = {
        ...validatedData,
        userId,
        cep: validatedData.cep.replace(/\D/g, ''), // Clean CEP
      };

      const address = await storage.createAddress(addressData);
      res.json(address);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          message: "Dados inv√°lidos",
          errors: error.errors
        });
      }
      console.error("Error creating address:", error);
      res.status(500).json({ message: "Erro ao criar endere√ßo" });
    }
  });

  app.put('/api/addresses/:id', isAuthenticated, async (req: any, res) => {
