      // Validate input using Zod schema (all fields optional for updates)
      const updateAddressSchema = z.object({
        name: z.string().min(1, "Nome √© obrigat√≥rio").optional(),
        cep: z.string().regex(/^\d{8}$|^\d{5}-?\d{3}$/, "CEP inv√°lido").optional(),
        street: z.string().min(1, "Rua √© obrigat√≥ria").optional(),
        number: z.string().min(1, "N√∫mero √© obrigat√≥rio").optional(),
        complement: z.string().optional(),
        neighborhood: z.string().min(1, "Bairro √© obrigat√≥rio").optional(),
        city: z.string().min(1, "Cidade √© obrigat√≥ria").optional(),
        state: z.string().min(2, "Estado √© obrigat√≥rio").max(2, "Estado deve ter 2 caracteres").optional(),
