import { Link } from "wouter";
import { useCart } from "@/contexts/CartContext";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { Heart, Plus } from "lucide-react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Product, Favorite } from "@shared/schema";

interface ProductCardProps {
  product: Product;
  featured?: boolean;
}

export default function ProductCard({ product, featured = false }: ProductCardProps) {
  const { addToCart } = useCart();
  const { isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch user's favorites
  const { data: favorites } = useQuery({
    queryKey: ["/api/favorites"],
    enabled: isAuthenticated,
  });

  // Check if this product is favorited
  const isFavorite = favorites && Array.isArray(favorites) 
    ? favorites.some((fav: any) => fav.productId === product.id)
    : false;

  // Favorites mutation
  const favoriteMutation = useMutation({
    mutationFn: async () => {
      if (isFavorite) {
        await apiRequest("DELETE", `/api/favorites/${product.id}`);
      } else {
        await apiRequest("POST", "/api/favorites", { productId: product.id });
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/favorites"] });
      toast({
        title: isFavorite ? "Removido dos favoritos" : "Adicionado aos favoritos",
        description: isFavorite 
          ? "Produto removido da sua lista de favoritos" 
          : "Produto adicionado √† sua lista de favoritos",
      });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel atualizar os favoritos",
        variant: "destructive",
      });
    },
  });

  const handleAddToCart = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    addToCart(product.id);
  };

  const handleToggleFavorite = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    if (!isAuthenticated) {
      toast({
        title: "Login necess√°rio",
        description: "Fa√ßa login para gerenciar seus favoritos",
        variant: "destructive",
      });
      return;
    }

    favoriteMutation.mutate();
  };

  return (
    <Link href={`/product/${product.id}`} className="block" data-testid={`card-product-${product.id}`}>
      <div className={`bg-card rounded-2xl overflow-hidden shadow-lg hover:shadow-xl transition-all duration-300 group cursor-pointer ${
        featured ? "lg:col-span-1" : ""
      }`}>
        <div className="relative">
          <img 
            src={product.imageUrl} 
            alt={product.name}
            className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
            data-testid={`img-product-${product.id}`}
          />
          <button 
            className="absolute top-3 right-3 bg-white/80 backdrop-blur-sm p-2 rounded-full hover:bg-white transition-colors"
            onClick={handleToggleFavorite}
            disabled={favoriteMutation.isPending}
            data-testid={`button-favorite-${product.id}`}
          >
            <Heart className={`h-4 w-4 transition-colors ${
              isFavorite 
                ? "text-red-500 fill-red-500" 
                : "text-muted-foreground hover:text-red-500"
            }`} />
          </button>
        </div>

        <div className="p-4">
          <h3 className="font-serif font-bold text-foreground mb-2" data-testid={`text-product-name-${product.id}`}>
            {product.name}
          </h3>
          <p className="text-sm text-muted-foreground mb-3 line-clamp-2" data-testid={`text-product-description-${product.id}`}>
            {product.description}
          </p>

          <div className="flex items-center justify-between">
            <span className="text-lg font-bold text-primary" data-testid={`text-product-price-${product.id}`}>
              R$ {parseFloat(product.price).toFixed(2).replace('.', ',')}
            </span>

            <div className="flex items-center space-x-2">

              <Button 
                size="sm" 
                onClick={handleAddToCart}
                className="bg-primary hover:bg-primary/90"
                data-testid={`button-add-cart-${product.id}`}
              >
                <Plus className="h-4 w-4 mr-1" />
                Adicionar
              </Button>
            </div>
          </div>
        </div>
      </div>
    </Link>
  );
}

client/src/contexts/CartContext.tsx.content:
import { createContext, useContext, useReducer, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { toast } from "@/hooks/use-toast";
import type { CartItem, Product } from "@shared/schema";

interface CartState {
  items: (CartItem & { product: Product })[];
  itemCount: number;
  total: number;
}

interface CartContextType extends CartState {
  addToCart: (productId: string, quantity?: number) => void;
  updateQuantity: (itemId: string, quantity: number) => void;
  removeFromCart: (itemId: string) => void;
  clearCart: () => void;
  isLoading: boolean;
}

const CartContext = createContext<CartContextType | null>(null);

type CartAction =
  | { type: "SET_ITEMS"; items: (CartItem & { product: Product })[] }
  | { type: "ADD_ITEM"; item: CartItem & { product: Product } }
  | { type: "UPDATE_ITEM"; itemId: string; quantity: number }
  | { type: "REMOVE_ITEM"; itemId: string }
  | { type: "CLEAR_CART" };

function cartReducer(state: CartState, action: CartAction): CartState {
  switch (action.type) {
    case "SET_ITEMS": {
      const items = action.items;
      const itemCount = items.reduce((sum, item) => sum + item.quantity, 0);
      const total = items.reduce((sum, item) => sum + parseFloat(item.product.price) * item.quantity, 0);
      return { items, itemCount, total };
    }
    case "ADD_ITEM": {
      const existingItemIndex = state.items.findIndex(item => item.productId === action.item.productId);
      let items;

      if (existingItemIndex >= 0) {
        items = [...state.items];
        items[existingItemIndex] = {
          ...items[existingItemIndex],
          quantity: items[existingItemIndex].quantity + action.item.quantity,
        };
      } else {
        items = [...state.items, action.item];
      }

      const itemCount = items.reduce((sum, item) => sum + item.quantity, 0);
      const total = items.reduce((sum, item) => sum + parseFloat(item.product.price) * item.quantity, 0);
      return { items, itemCount, total };
    }
    case "UPDATE_ITEM": {
      const items = state.items.map(item =>
        item.id === action.itemId ? { ...item, quantity: action.quantity } : item
      );
      const itemCount = items.reduce((sum, item) => sum + item.quantity, 0);
      const total = items.reduce((sum, item) => sum + parseFloat(item.product.price) * item.quantity, 0);
      return { items, itemCount, total };
    }
    case "REMOVE_ITEM": {
      const items = state.items.filter(item => item.id !== action.itemId);
      const itemCount = items.reduce((sum, item) => sum + item.quantity, 0);
      const total = items.reduce((sum, item) => sum + parseFloat(item.product.price) * item.quantity, 0);
      return { items, itemCount, total };
    }
    case "CLEAR_CART":
      return { items: [], itemCount: 0, total: 0 };
    default:
      return state;
  }
}

export function CartProvider({ children }: { children: React.ReactNode }) {
  const { isAuthenticated } = useAuth();
  const queryClient = useQueryClient();

  const [state, dispatch] = useReducer(cartReducer, {
    items: [],
    itemCount: 0,
    total: 0,
  });

  const { data: cartItems, isLoading } = useQuery({
    queryKey: ["/api/cart"],
    enabled: isAuthenticated,
  });

  useEffect(() => {
    if (cartItems && Array.isArray(cartItems)) {
      dispatch({ type: "SET_ITEMS", items: cartItems });
    } else if (!isAuthenticated) {
      // Reset cart when user is not authenticated
      dispatch({ type: "CLEAR_CART" });
    }
  }, [cartItems, isAuthenticated]);

  const addToCartMutation = useMutation({
    mutationFn: async ({ productId, quantity = 1 }: { productId: string; quantity?: number }) => {
      await apiRequest("POST", "/api/cart", { productId, quantity });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Sucesso!",
        description: "Produto adicionado ao carrinho",
      });
    },
    onError: (error) => {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel adicionar o produto ao carrinho",
        variant: "destructive",
      });
    },
  });

  const updateQuantityMutation = useMutation({
    mutationFn: async ({ itemId, quantity }: { itemId: string; quantity: number }) => {
      await apiRequest("PUT", `/api/cart/${itemId}`, { quantity });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel atualizar a quantidade",
        variant: "destructive",
      });
    },
  });

  const removeFromCartMutation = useMutation({
    mutationFn: async (itemId: string) => {
      await apiRequest("DELETE", `/api/cart/${itemId}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Produto removido",
        description: "O produto foi removido do carrinho",
      });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel remover o produto",
        variant: "destructive",
      });
    },
  });

  const clearCartMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("DELETE", "/api/cart");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      dispatch({ type: "CLEAR_CART" });
    },
  });

  const contextValue: CartContextType = {
    ...state,
    addToCart: (productId: string, quantity = 1) => {
      if (!isAuthenticated) {
        toast({
          title: "Login necess√°rio",
          description: "Fa√ßa login para adicionar produtos ao carrinho",
          variant: "destructive",
        });
        return;
      }
      addToCartMutation.mutate({ productId, quantity });
    },
    updateQuantity: (itemId: string, quantity: number) => {
      updateQuantityMutation.mutate({ itemId, quantity });
    },
    removeFromCart: (itemId: string) => {
      removeFromCartMutation.mutate(itemId);
    },
    clearCart: () => {
      clearCartMutation.mutate();
    },
    isLoading: isLoading || addToCartMutation.isPending || updateQuantityMutation.isPending || removeFromCartMutation.isPending,
  };

  return (
    <CartContext.Provider value={contextValue}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error("useCart must be used within a CartProvider");
  }
  return context;
}

